/* fungsi untuk refresh data setelah hapus data transaksi */
void refresh_data_transaksi(){
    // tutup file data_transaksi
    fclose(data_transaksi);

    // buka file data_transaksi dan buat file baru
    data_transaksi=fopen("data_transaksi.txt","w+");
    // tutup file data_transaksi
    fclose(data_transaksi);

    // buka file data_transaksi untuk ditambahkan
    data_transaksi=fopen("data_transaksi.txt","a+");

    // print data transaksi ke file
    fprintf(data_transaksi,"Data Transaksi\n\n");

    b=1;
    while(b<=a){
    // aksi selama indeks belum mencapai data terakhir

        // print data transaksi ke file
        fprintf(data_transaksi,"Tgl Transaksi   : %s\n",tgl_transaksi[b]);
        fprintf(data_transaksi,"Kode Transaksi  : %d\n",kode[b]);
        fprintf(data_transaksi,"Username        : %s\n",username_transaksi[b]);
        fprintf(data_transaksi,"Produk          : %s\n",nm_produk[b]);
        fprintf(data_transaksi,"Jumlah          : %d\n",jumlah[b]);
        fprintf(data_transaksi,"Harga satuan    : %d\n",harga[b]);
        fprintf(data_transaksi,"PPN             : %d\n",ppn[b]);
        fprintf(data_transaksi,"Total harga     : %d\n\n",total_harga[b]);

        // indeks menuju data berikutnya
        b++;
    }
}

/* fungsi untuk mencari data sesuai dengan kode transaksi yang diinput */
void analisis_data_transaksi(){
    b=0;
    d=0;

    while(b<=a && d==0){
    // aksi selama data belum ditemukan

        // indeks menuju data berikutnya
        b++;

        if(input_kode==kode[b]){
        // aksi jika data ditemukan

            // hentikan perulangan
            d++;
        }
    }
}

