/* fungsi untuk refresh data setelah hapus data pelanggan */
void refresh_data_pelanggan(){
    // tutup file data_pelanggan
    fclose(data_pelanggan);

    // buka file data_pelanggan untuk membuat file baru
    data_pelanggan=fopen("data_pelanggan.txt","w+");

    // tutup file data_pelanggan
    fclose(data_pelanggan);

    // buka file data_pelanggan untuk ditambahkan
    data_pelanggan=fopen("data_pelanggan.txt","a+");

    // print data pelanggan ke file data_pelanggan
    fprintf(data_pelanggan,"Data Pelanggan\n\n");

    b=1;
    while(b<=g){
        // aksi selama indeks belum mencapai data terakhir

        // print data pelanggan ke file data_pelanggan
        fprintf(data_pelanggan,"Username        : %s\n",username[b]);
        fprintf(data_pelanggan,"Password        : %s\n",password[b]);
        fprintf(data_pelanggan,"Email           : %s\n",email[b]);
        fprintf(data_pelanggan,"Nama pelanggan  : %s\n",nm_pelanggan[b]);
        fprintf(data_pelanggan,"Umur pelanggan  : %d\n",umur[b]);
        fprintf(data_pelanggan,"Alamat pelanggan: %s\n\n",alamat[b]);

        // indeks menuju data berikutnya
        b++;
    }
}

