/* fungsi untuk validasi email */
int validasi_email(){
    b=1;

    if(g!=0){
    // aksi jika sudah ada data

        while(b<=g){
        // aksi selama indeks belum mencapai data terakhir

            if(strcmp(email[0],email[b])!=0){
            // aksi jika email belum digunakan

                if(b==g){
                // aksi jika indeks sudah mencapai data terakhir
                    return TRUE;
                }

            }else if(strcmp(email[0],email[b])==0){
            // jika email sudah digunakan
                return FALSE;
            }

            // indeks menuju data berikutnya
            b++;
        }

    }else{
    // aksi jika tidak ada data
        return TRUE;
    }
}

/* fungsi untuk validasi username */
int validasi_username(){
    b=1;

    if(g!=0){
    // aksi jika sudah ada data

        while(b<=g){
        // aksi selama indeks belum mencapai data terakhir

            if(strcmp(username[0],username[b])!=0){
            // aksi jika username belum digunakan
                if(b==g){
                // aksi jika indeks sudah mencapai data terakhir
                    return TRUE;
                }

            }else if(strcmp(username[0],username[b])==0){
            // jika username sudah digunakan
                return FALSE;
            }

            // indeks menuju data berikutnya
            b++;
        }

    }else{
    // aksi jika belum ada data
        return TRUE;
    }
}

