/* fungsi untuk validasi login */
int validasi_login(){
    // inisialisasi indeks
    b=1;

    if(g!=0){
    // aksi jika ada data
        while(b<=g){
        // aksi selama indeks belum mencapai data terakhir

            if(strcmp(masuk_login,username[b])!=0 && strcmp(pass_login,password[b])!=0){
            // aksi jika username dan password yang diinput user salah

                if(b==g){
                // aksi jika indeks sudah mencapai data terakhir
                    return FALSE;
                }

            }else if(strcmp(masuk_login,username[b])==0 && strcmp(pass_login,password[b])==0){
            // aksi jika username dan password yang diinput user benar
                return TRUE;
            }

            // indeks untuk menuju data transaksi berikutnya
            b++;
        }
    }else{
    // aksi jika tidak ada data
        return FALSE;
    }
}

