/* fungsi untuk memasukkan data transaksi dari file data transaksi ke variabel */
void cek_data_transaksi(){
    // inisialsiasi indeks data transaksi
    a=0;

    // ulangi file untuk menuju ke data transaksi teratas
    rewind(data_transaksi);

    if(fgets(baris,sizeof(baris),data_transaksi)==NULL){
    // aksi jika data transaksi tidak ada

        // inisialsiasi indeks data transaksi
        a=0;

        fprintf(data_transaksi,"Data Transaksi\n\n");

    }else{
    // aksi jika data transaksi ada

        while(!feof(data_transaksi)){
        // aksi selama file data transaksi belum mencapai batas akhir

            // indeks untuk menuju data transaksi berikutnya
            a++;

            // masukkan data transaksi dari file data transaksi ke variabel
            fscanf(data_transaksi,"\nTgl Transaksi   : %[^\n]%*c\n",tgl_transaksi[a]);
            fscanf(data_transaksi,"Kode Transaksi  : %d\n",&kode[a]);
            fscanf(data_transaksi,"Username        : %[^\n]%*c\n\n",username_transaksi[a]);
            fscanf(data_transaksi,"Produk          : %[^\n]%*c\n",nm_produk[a]);
            fscanf(data_transaksi,"Jumlah          : %d\n",&jumlah[a]);
            fscanf(data_transaksi,"Harga satuan    : %d\n",&harga[a]);
            fscanf(data_transaksi,"PPN             : %d\n",&ppn[a]);
            fscanf(data_transaksi,"Total harga     : %d\n\n",&total_harga[a]);
        }

        // perbarui template kode dengan inisialisasi kode transaksi
        template_kode=kode[a];
    }
}

